mapper.insert:
insert
	into tb_board
	(
	    board_no
	  , category
	  , board_title
	  , board_writer
	  , board_content
	  , origin_name
	  , change_name
	)
	values
	(
		SEQ_BONO.nextval
	  , #{category}	
	  , #{boardTitle}	
	  , #{boardWriter}	
	  , #{boardContent}
	  , #{originName}	
	  , #{changeName}		
	)

mapper.update:
update
       tb_board
   set board_title = #{boardTitle}
   	 , category = #{category}	
     , board_content = #{boardContent}
     , origin_name = #{originName}
     , change_name = #{changeName}
 where board_no = #{boardNo}

mapper.insert:
insert
	into tb_reply
	(
	reply_no
  , reply_content
  , post_no
  , member_no
  )
  values
  (
    SEQ_RPNO.nextval
  , #{replyContent}
  , #{postNo}
  , #{memberNo} 
  )

mapper.update:
update
       tb_reply
   set reply_content = #{replyContent}
 where reply_no = #{replyNo}

mapper.insert:
insert
	into tb_report
	(
	    report_no
	  , post_no
	  , member_no
	  , report_content
	)
	values
	(
		SEQ_RENO.nextval
	  , #{postNo}	
	  , #{memberNo}	
	  , #{reportContent}	

	)

mapper.delete:
delete
  from tb_likeboard
 where member_no = #{memberNo}
   and board_no = #{boardNo}
