mapper.update:
update
	   tb_chatmember
   set not_read = 0
 where chatroom_no = #{roomNo}
   and member_no = ${myNo}

mapper.insert:

	SELECT SEQ_CHCNO.CURRVAL "CONTENT_NO" FROM DUAL

insert
  into tb_chatcontent
  (
  	chatcontent_no
  , chatroom_no
  , writer_no
  , chat_content
  )
  values
  (
  	seq_chcno.nextval
  , #{roomNo}
  , #{writerNo}
  , #{content}
  )

mapper.update:
update
	   tb_chatmember
   set not_read = not_read + 1
 where chatroom_no =#{roomNo}
   and member_no != #{writerNo}

mapper.select:
select
       m.chatroom_no
  from tb_chatmember m
  join tb_chatmember mm on (m.chatroom_no = mm.chatroom_no)
 where m.member_no = #{writerNo}
   and mm.member_no = #{otherNo}
