mapper.insert:
insert
  into tb_project
  (
  	project_no
  , project_title
  , project_content
  , public_status
  ) 
  values
  (
  	seq_pjno.nextval
  , #{projectTitle}
  , #{projectContent}
  , #{publicStatus}
  )

insert
  into tb_like
  (
  	member_no
  , project_no
  , like_date
  )
  values
  (
  	#{memberNo}
  , #{projectNo}
  , sysdate
  )

mapper.delete:
delete
  from tb_like
 where member_no = #{memberNo}
   and project_no = #{projectNo}

mapper.select:
select
	   count(*)
  from tb_projectmember
 where project_no = #{projectNo}
   and member_no = #{memberNo}
   and status = 2

mapper.insert:
insert
  into tb_list
  (
  	list_no
  , project_no
  , member_no
  , list_title
  , begin_date
  , end_date
  , color
  )
  values
  (
  	seq_lino.nextval
  , #{projectNo}
  , #{memberNo}
  , #{listTitle}
  , #{beginDate}
  , #{endDate}
  , #{color}
  )
