mapper.insert:
insert
  into tb_project
  (
  	project_no
  , owner_no
  , project_title
  , project_content
  , public_status
  ) 
  values
  (
  	seq_pjno.nextval
  , #{owner}
  , #{projectTitle}
  , #{projectContent}
  , #{publicStatus}
  )

insert
  into tb_like
  (
  	member_no
  , project_no
  , like_date
  )
  values
  (
  	#{memberNo}
  , #{projectNo}
  , sysdate
  )

mapper.delete:
delete
  from tb_like
 where member_no = #{memberNo}
   and project_no = #{projectNo}
